class Solution:
    def tilingRectangle(self, n: int, m: int) -> int:
        dp = [[0] * (m + 1) for _ in range(n + 1)]

        for i in range(1, n + 1):
            for j in range(1, m + 1):
                if i == 1 and j == 1:
                    dp[i][j] = 1
                elif i == 1:
                    dp[i][j] = dp[i][j - 1] + 1
                elif j == 1:
                    dp[i][j] = dp[i - 1][j] + 1
                else:
                    dp[i][j] = min(dp[i - 1][j] + 1, dp[i][j - 1] + 1)

        return dp[n][m]
