        memo = {}

        def dp(n, m):
            if (n, m) in memo:
                return memo[(n, m)]
            if n == 1:
                return m
            if m == 1:
                return n
            if n > m:
                return dp(n, m - 1) + 1
            if n >= m:
                ans = float('inf')
                for i in range(1, n + 1):
                    ans = min(ans, dp(i, m // i) + (m // i - 1))
                return ans
            return dp(n - 1, m) + 1

