from typing import List

class Solution:
    def minSumOfLengths(self, arr: List[int], target: int) -> int:
        n = len(arr)
        prefix_sum = [0] * (n + 1)
        hashmap = {}
        min_length = float('inf')

        # Calculate prefix sum and store in hashmap
        for i in range(n):
            prefix_sum[i + 1] = prefix_sum[i] + arr[i]
            hashmap[prefix_sum[i + 1]] = i + 1

        # Iterate over the array and find sub-arrays
        for i in range(n):
            complement = target - prefix_sum[i + 1]
            if complement in hashmap and hashmap[complement] > i:
                min_length = min(min_length, i + 1 + hashmap[complement])

        # If no sub-array is found, return -1
        if min_length == float('inf'):
            return -1
        else:
            return min_length
