def factor_sum(n):
    def prime_factors(num):
        i = 2
        factors = []
        while i * i <= num:
            if num % i:
                i += 1
            else:
                num //= i
                factors.append(i)
        if num > 1:
            factors.append(num)
        return factors

    result = n
    while True:
        result = sum(prime_factors(result))
        if result == n:
            break
        n = result
    return result

