from typing import List

class Solution:
    def braceExpansionII(self, expression: str) -> List[str]:
        stack = []
        for char in expression:
            if char == '{':
                stack.append(char)
            elif char == '}':
                temp = []
                while stack and stack[-1]!= '{':
                    temp.append(stack.pop())
                stack.pop()
                stack.append(temp)
            elif char == ',':
                continue
            else:
                stack.append(char)

        # Perform the expansion
        words = []
        def expand(stack):
            if not stack:
                return
            temp = []
            while stack:
                if stack[-1] == '{':
                    stack.pop()
                    temp.extend(expand(stack))
                elif stack[-1] == ',':
                    stack.pop()
                    temp.extend(expand(stack))
                else:
                    temp.append(stack.pop())
            words.extend(temp)
        expand(stack)

        # Remove duplicates and sort the words
        return sorted(set(words))
```

