class FastIOBuffer(io.BytesIO):
    def __init__(self, file):
        io.BytesIO.__init__(self, file.read())
        self._fd = file.fileno()
        self.writable = "x" in file.mode or "r" not in file.mode
        self.write = super(FastIOBuffer, self).write if self.writable else None

    def readline(self):
        return self.readline(self.size)

    def read(self, size=-1):
        if size < 0:
            size = self.size
        pos = self.tell()
        self.seek(0, 2), size = min(size, self.size), self.seek(0)
        return self.readUpTo(size)

    def readUpTo(self, n):
        data = bytes()
        while len(data) < n:
            b = self.read(100000)
            if not b:
                break
            data += b
        return data[:n]

    def flush(self):
        if self.writable:
            os.write(self._fd, self.getvalue())
            self.truncate(0), self.seek(0)

    @property
    def size(self):
        return self.tell()

    @property
    def writable(self):
        return self._writable

    @property
    def _writable(self):
        if not hasattr(self, "_fd"):
            return False
        return os.fstat(self._fd).st_mode & (os.O_WRONLY | os.O_RDWR) == os.O_WRONLY

