class Solution:
    def isValid(self, code: str) -> bool:

        stack = []
        i = 0
        while i < len(code):
            if code[i] == '<':
                j = i + 1
                while code[j]!= '>':
                    j += 1
                tag = code[i + 1:j]
                if tag[0]!= '/':
                    stack.append(tag[1:])
                else:
                    if not stack or stack.pop()!= tag[2:]:
                        return False
                i = j + 1
            else:
                i += 1

        return not stack

