def peaceful_yard(yard, min_distance):
    # Convert the yard string into a 2D list
    yard = [list(row) for row in yard]

    # Initialize variables to keep track of cat positions
    lou, mustache_cat, raoul = None, None, None

    # Find the positions of Lou, Mustache Cat, and Raoul
    for i in range(len(yard)):
        for j in range(len(yard[i])):
            if yard[i][j].upper() == 'L':
                lou = (i, j)
            elif yard[i][j].upper() == 'M':
                mustache_cat = (i, j)
            elif yard[i][j].upper() == 'R':
                raoul = (i, j)

    # Check the distances between all pairs of cats
    for i in range(len(yard)):
        for j in range(len(yard[i])):
            if yard[i][j] == '-':
                continue
            cat = yard[i][j].upper()
            if cat == 'L':
                if lou is None:
                    continue
                distance = abs(i - lou[0]) + abs(j - lou[1])
            elif cat == 'M':
                if mustache_cat is None:
                    continue
                distance = abs(i - mustache_cat[0]) + abs(j - mustache_cat[1])
            elif cat == 'R':
                if raoul is None:
                    continue
                distance = abs(i - raoul[0]) + abs(j - raoul[1])
            else:
                continue

            # Check if the distance is smaller than the minimum distance
            if (cat == 'L' and lou is not None and distance < min_distance) or \
               (cat == 'M' and mustache_cat is not None and distance < min_distance) or \
               (cat == 'R' and raoul is not None and distance < min_distance):
                return False

    # If no conflicts are found, return True
    return True

