            if distance < min_distance:
                return False
    return True

def is_valid(number):
    digit_sum = sum(int(digit) for digit in str(number))
    return number % digit_sum == 0

def get_next(number):
    number += 1
    while not is_valid(number):
        number += 1
    return number

def get_series(start, n):
    series = []
    number = start
    while len(series) < n:
        if is_valid(number):
            series.append(number)
        number += 1
    return series[:n]

