import sys
from collections import defaultdict

def solve():
    n = int(input())
    pos = []
    valid = []
    for _ in range(n):
        x, y, f1, f2, f3, f4 = map(int, input().split())
        pos.append((x, y))
        valid.append((f1, f2, f3, f4))

    def can_reach(x, y):
        for i in range(n):
            if (valid[i][0] and x <= pos[i][0]) or (valid[i][1] and y <= pos[i][1]) or (valid[i][2] and x >= pos[i][0]) or (valid[i][3] and y >= pos[i][1]):
                return True
        return False

    def get_range():
        min_x, min_y = 10**5, 10**5
        max_x, max_y = -10**5, -10**5
        for i in range(n):
            min_x = min(min_x, pos[i][0])
            min_y = min(min_y, pos[i][1])
            max_x = max(max_x, pos[i][0])
            max_y = max(max_y, pos[i][1])
        return min_x, min_y, max_x, max_y

    def get_middle(x, y):
        min_x, min_y, max_x, max_y = get_range()
        if can_reach(x, y):
            return True
        else:
            if can_reach(x, min_y) and can_reach(x, max_y) and can_reach(min_x, y) and can_reach(max_x, y):
                return True
            else:
                return False

    for x in range(-10**5, 10**5):
        for y in range(-10**5, 10**5):
            if get_middle(x, y):
                print(1, x, y)
                return

    print(0)

if __name__ == '__main__':
    t = 1
    for _ in range(t):
        solve()

