```python
import math

def is_prime(n):
    """Check if a number is prime."""
    if n < 2:
        return False
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def prime_factorization(n):
    """Find prime factors of a number and their powers."""
    factors = {}
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            power = 0
            while n % i == 0:
                n //= i
                power += 1
            factors[i] = power
    if n > 1:
        factors[n] = 1
    return factors

def calculate_sum(primes, powers, min_powers):
    """Calculate the sum of numbers that contain all primes with minimum powers."""
    total_sum = 0
    max_power = max(powers.values())
    for power in range(1, max_power + 1):
        num = 1
        for prime, prime_power in primes.items():
            num *= (prime ** prime_power)
        for prime, min_power in min_powers.items():
            if prime_power > min_power:
                break
        else:
            total_sum += num
    return total_sum % (10**9 + 7)

def solve_test_case():
    n = int(input())
    primes = list(map(int, input().split()))
    powers = list(map(int, input().split()))
    min_powers = list(map(int, input().split()))
    return calculate_sum(primes, powers, min_powers)

def main():
    t = int(input())
    for _ in range(t):
        print(solve_test_case())

if __name__ == "__main__":
    main()
