class Solution:
    def distinctEchoSubstrings(self, text: str) -> int:
        seen = set()
        n = len(text)

        for i in range(n):
            for j in range(i + 1, n):
                substr = text[i:j + 1]
                if substr + substr in text:
                    seen.add(substr + substr)

        return len(seen)
