class Solution:
    def findAndReplacePattern(self, words: List[str], pattern: str) -> List[str]:
        def match(word: str) -> bool:
            mapping = {}
            for p, w in zip(pattern, word):
                if p not in mapping:
                    if w in mapping.values():
                        return False
                    mapping[p] = w
                elif mapping[p]!= w:
                    return False
            return True

        return [word for word in words if match(word)]

