def calculate_max_calories(n, m, a):
    # Calculate Iahub's path
    dp1 = [[0] * m for _ in range(n)]
    dp1[0][0] = a[0][0]
    for i in range(n):
        for j in range(m):
            if i == 0 and j == 0:
                continue
            if i == 0:
                dp1[i][j] = dp1[i][j-1] + a[i][j]
            elif j == 0:
                dp1[i][j] = dp1[i-1][j] + a[i][j]
            else:
                dp1[i][j] = max(dp1[i-1][j], dp1[i][j-1]) + a[i][j]

    # Calculate Iahubina's path
    dp2 = [[0] * m for _ in range(n)]
    dp2[n-1][0] = a[n-1][0]
    for i in range(n-1, -1, -1):
        for j in range(m):
            if i == n-1 and j == 0:
                continue
            if i == n-1:
                dp2[i][j] = dp2[i][j-1] + a[i][j]
            elif j == 0:
                dp2[i][j] = dp2[i+1][j] + a[i][j]
            else:
                dp2[i][j] = max(dp2[i+1][j], dp2[i][j-1]) + a[i][j]

    # Calculate Iahub's path from right to left
    dp3 = [[0] * m for _ in range(n)]
    dp3[0][m-1] = a[0][m-1]
    for i in range(n):
        for j in range(m-1, -1, -1):
            if i == 0 and j == m-1:
                continue
            if i == 0:
                dp3[i][j] = dp3[i][j+1] + a[i][j]
            elif j == m-1:
                dp3[i][j] = dp3[i-1][j] + a[i][j]
            else:
                dp3[i][j] = max(dp3[i-1][j], dp3[i][j+1]) + a[i][j]

    # Calculate Iahubina's path from bottom to top
    dp4 = [[0] * m for _ in range(n)]
    dp4[n-1][m-1] = a[n-1][m-1]
    for i in range(n-1, -1, -1):
        for j in range(m-1, -1, -1):
            if i == n-1 and j == m-1:
                continue
            if i == n-1:
                dp4[i][j] = dp4[i][j+1] + a[i][j]
            elif j == m-1:
                dp4[i][j] = dp4[i+1][j] + a[i][j]
            else:
                dp4[i][j] = max(dp4[i+1][j], dp4[i][j+1]) + a[i][j]

    # Find the meeting point
    max_calories = 0
    for i in range(1, n-1):
        for j in range(1, m-1):
            max_calories = max(max_calories, dp1[i-1][j] + dp2[i+1][j] + dp3[i][j-1] + dp4[i][j+1])

    for i in range(1, n-1):
        for j in range(1, m-1):
            max_calories = max(max_calories, dp1[i][j-1] + dp2[i][j+1] + dp3[i+1][j] + dp4[i-1][j])

    return max_calories

