def champernowneDigit(n):
    if not isinstance(n, int) or n <= 0:
        return float('nan')

    length = 1
    count = 9
    start = 1

    while n > length * count:
        n -= length * count
        length += 1
        count *= 10
        start *= 10

    start += (n - 1) // length
    s = str(start)
    return int(s[(n - 1) % length])
