class Solution:
    def braceExpansionII(self, expression: str) -> List[str]:
        def distribute(L1: List[str], L2: List[str]) -> List[str]:
            """Distribute two lists of strings."""
            if not L1 or not L2:
                return []
            result = set()
            for x in L1:
                for y in L2:
                    result.add(x + y)
            return sorted(list(result))

        def parse(expression: str) -> List[str]:
            """Parse the expression and return a list of strings."""
            if not expression:
                return []
            if expression[0]!= '{':
                # If the expression does not start with '{', it must be a single character.
                return [expression]
            if ',' not in expression:
                # If there is no comma, it must be a single set.
                return parse(expression[1:-1])
            i = 1
            balance = 0
            j = 1
            while i < len(expression):
                if expression[i] == '{':
                    balance += 1
                elif expression[i] == '}':
                    balance -= 1
                elif expression[i] == ',' and balance == 0:
                    # We have found a comma at the top level.
                    break
                i += 1
            # Split the expression into two parts at the comma.
            L1 = parse(expression[1:i])
            L2 = parse(expression[i+1:-1])
            # Distribute the two lists.
            return sorted(set(L1 + L2))

        def parse_with_distribute(expression: str) -> List[str]:
            """Parse the expression and distribute the sets."""
            if not expression:
                return []
            if expression[0] == '{':
                # If the expression starts with '{', parse the set and distribute.
                return parse(expression)
            i = 0
            result = []
            while i < len(expression):
                if expression[i] == '{':
                    # If we encounter a '{', parse the set and distribute.
