class Solution:
    def findPairs(self, nums: List[int], k: int) -> int:
        if k < 0:
            return 0

        num_set = set()
        pair_set = set()

        for num in nums:
            if num - k in num_set:
                pair_set.add((min(num, num - k), max(num, num - k)))
            if num + k in num_set:
                pair_set.add((min(num, num + k), max(num, num + k)))
            num_set.add(num)

