    if n < 2:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

def get_neighbors(state):
    """Get all possible next states by swapping adjacent tiles."""
    neighbors = []
    for i in range(3):
        for j in range(3):
            # Check all four directions (up, down, left, right)
            for di, dj in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                ni, nj = i + di, j + dj
                if 0 <= ni < 3 and 0 <= nj < 3:
                    # Swap tiles if their sum is prime
                    if is_prime(state[i*3 + j] + state[ni*3 + nj]):
                        # Create a new state by swapping the tiles
                        new_state = state[:]
                        new_state[i*3 + j], new_state[ni*3 + nj] = new_state[ni*3 + nj], new_state[i*3 + j]
                        neighbors.append(new_state)
    return neighbors

def bfs(initial_state, goal_state):
    """Perform BFS to find the shortest path to the goal state."""
    queue = deque([(initial_state, 0)])
    visited = set(tuple(initial_state))
    while queue:
        state, steps = queue.popleft()
        if state == goal_state:
            return steps
        for neighbor in get_neighbors(state):
            if tuple(neighbor) not in visited:
                queue.append((neighbor, steps + 1))
                visited.add(tuple(neighbor))
    return -1

