def prime_factors(n):
    """Return a list of prime factors of n"""
    i = 2
    factors = []
    while i * i <= n:
        if n % i:
            i += 1
        else:
            n //= i
            factors.append(i)
    if n > 1:
        factors.append(n)
    return factors

def divisors(n):
    """Return a list of divisors of n"""
    divs = [1]
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            divs.extend([i, n // i])
    if n**0.5 == int(n**0.5):
        divs.pop()
    divs.append(n)
    return sorted(list(set(divs)))

def ds_multof_pfs(n_min, n_max):
    """Return a sorted list of numbers whose sum of divisors is divisible by the sum of their prime factors"""
    result = []
    for num in range(n_min, n_max + 1):
        pfs = prime_factors(num)
        ds = divisors(num)
        if sum(ds) % sum(pfs) == 0:
            result.append(num)
    return result

