def complete_binary_tree(a):
    # Create a binary tree from the input array
    class TreeNode:
        def __init__(self, val=0, left=None, right=None):
            self.val = val
            self.left = left
            self.right = right

    def insert_node(root, val):
        if not root:
            return TreeNode(val)
        if val < root.val:
            root.left = insert_node(root.left, val)
        else:
            root.right = insert_node(root.right, val)
        return root

    root = None
    for val in a:
        root = insert_node(root, val)

    # Perform a breadth-first traversal of the binary tree
    result = []
    queue = [root]
    while queue:
        node = queue.pop(0)
        result.append(node.val)
        if node.left:
            queue.append(node.left)
        if node.right:
            queue.append(node.right)

    return result


