                j = code.find('>', i)
                if j == -1:
                    return False
                tag_name = code[i+1:j]
                if '/' in tag_name:
                    if not stack or stack[-1]!= tag_name[1:]:
                        return False
                    stack.pop()
                else:
                    if len(tag_name) < 1 or len(tag_name) > 9 or not tag_name.isupper():
                        return False
                    stack.append(tag_name)
                i = j + 1
            elif code[i:i+9] == '<![CDATA[':
                j = code.find(']]>', i)
                if j == -1:
                    return False
                i = j + 3
            else:
                i += 1
        return not stack
