class Solution:
    def numberToWords(self, num: int) -> str:
        def one(num):
            """Convert a single digit to words."""
            switcher = {
                1: 'One',
                2: 'Two',
                3: 'Three',
                4: 'Four',
                5: 'Five',
                6: 'Six',
                7: 'Seven',
                8: 'Eight',
                9: 'Nine'
            }
            return switcher.get(num)

        def two_less_20(num):
            """Convert a two-digit number less than 20 to words."""
            switcher = {
                10: 'Ten',
                11: 'Eleven',
                12: 'Twelve',
                13: 'Thirteen',
                14: 'Fourteen',
                15: 'Fifteen',
                16: 'Sixteen',
                17: 'Seventeen',
                18: 'Eighteen',
                19: 'Nineteen'
            }
            return switcher.get(num)

        def ten(num):
            """Convert a tens digit to words."""
            switcher = {
                2: 'Twenty',
                3: 'Thirty',
                4: 'Forty',
                5: 'Fifty',
                6: 'Sixty',
                7: 'Seventy',
                8: 'Eighty',
                9: 'Ninety'
            }
            return switcher.get(num)

        def two(num):
            """Convert a two-digit number to words."""
            if not num:
                return ''
            elif num < 10:
                return one(num)
            elif num < 20:
                return two_less_20(num)
            else:
                tens_digit = num // 10
                ones_digit = num % 10
                return ten(tens_digit) +'' + one(ones_digit) if ones_digit else ten(tens_digit)

        def three(num):
            """Convert a three-digit number to words."""
            hundred_digit = num // 100
            rest = num % 100
            if hundred_digit and rest:
                return one(hundred_digit) +'Hundred'+ two(rest)
            elif not hundred_digit and rest:
                return two(rest)
            elif hundred_digit and not rest:
                return one(hundred_digit) +'Hundred'

        billion = num // 1000000000
        million = (num - billion * 1000000000) // 1000000
        thousand = (num - billion * 1000000000 - million * 1000000) // 1000
        rest = num - billion * 1000000000 - million * 1000000 - thousand * 1000

        if not num:
            return 'Zero'

        result = ''
        if billion:
            result = three(billion) +'Billion'
        if million:
            result +='' if result else ''
            result += three(million) +'Million'
        if thousand:
            result +='' if result else ''
            result += three(thousand) +'Thousand'
        if rest:
            result +='' if result else ''
            result += three(rest)
        return result

