class Harshad:
    @staticmethod
    def is_valid(number):
        """Check if a number is a Harshad number."""
        return number % sum(int(digit) for digit in str(number)) == 0

    @staticmethod
    def get_next(number):
        """Get the next Harshad number."""
        next_number = number + 1
        while not Harshad.is_valid(next_number):
            next_number += 1
        return next_number

    @staticmethod
    def get_series(count, start=0):
        """Get a series of Harshad numbers."""
        series = []
        next_number = Harshad.get_next(start)
        for _ in range(count):
            series.append(next_number)
            next_number = Harshad.get_next(next_number)
        return series


