def build_trie(*words):
    """
    Builds a trie (prefix tree) using dictionaries.

    Args:
        *words: Variable number of words to be inserted into the trie.

    Returns:
        A dictionary representing the trie.
    """
    trie = {}
    for word in words:
        if not word:  # Handle empty input
            continue
        node = trie
        for i in range(1, len(word) + 1):
            prefix = word[:i]
            if prefix not in node:
