from collections import deque

class Solution:
    def integerReplacement(self, n: int) -> int:
        queue = deque([(n, 0)])
        visited = set([n])

        while queue:
            current, steps = queue.popleft()

            if current == 1:
                return steps

            if current % 2 == 0:
                next_num = current // 2
                if next_num not in visited:
                    queue.append((next_num, steps + 1))
                    visited.add(next_num)
