class Solution:
    def lengthLongestPath(self, input: str) -> int:
        paths = input.split('\n')
        stack = [0]
        max_length = 0

        for path in paths:
            level = path.count('\t')
            name = path.lstrip('\t')

            while len(stack) > level + 1:
                stack.pop()

            if '.' in name:
                max_length = max(max_length, stack[-1] + len(name))
            else:
                stack.append(stack[-1] + len(name) + 1)

