        self.size = [1] * n
        self.speed = [1] * n
        self.teeth = [1] * n
        self.blocked = [False] * n

    def find(self, x):
        if self.parent[x]!= x:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

