from itertools import chain, combinations

def powerset(iterable):
    "powerset([1,2,3]) --> () (1,) (2,) (3,) (1,2) (1,3) (2,3) (1,2,3)"
    s = list(iterable)
    return chain.from_iterable(combinations(s, r) for r in range(len(s)+1))

def is_connected(graph, subset):
    if not subset:
        return True
    visited = set()
    stack = [subset[0]]
    while stack:
        node = stack.pop()
        if node not in visited:
            visited.add(node)
            for neighbor in graph[node]:
                if neighbor in subset and neighbor not in visited:
                    stack.append(neighbor)
    return len(visited) == len(subset)

def calculate_density(graph, subset):
    node_sum = sum(graph['nodes'][node] for node in subset)
    edge_sum = sum(graph['edges'].get((node, neighbor), graph['edges'].get((neighbor, node))) for node in subset for neighbor in subset if node < neighbor and (node, neighbor) in graph['edges'] or (neighbor, node) in graph['edges'])
    if edge_sum == 0:
        return 0
    return node_sum / edge_sum

def find_max_density(graph):
    max_density = 0
    for subset in powerset(graph['nodes']):
        if is_connected(graph, subset):
            density = calculate_density(graph, subset)
            max_density = max(max_density, density)
    return max_density

