def solution():
    import sys
    from bisect import bisect_right

    def read_ints():
        return list(map(int, sys.stdin.readline().strip().split()))

    n, m, a, d = read_ints()
    t = sorted(read_ints())

    employees = set()
    for i in range(1, n + 1):
        employees.add(i * a)

    t = sorted(t)
    events = []
    for ti in t:
        lb = bisect_right(employees, ti)
        if lb == 0 or employees[lb - 1] >= ti:
            continue
        ub = lb
        events.append((ti, 'C'))
        events.append((ti + d, 'E'))
        while ub < len(employees) and employees[ub] <= ti + d:
            if employees[ub] == ti + d:
                if ub > 0 and events[ub - 1][0] == employees[ub - 1] - d:
                    events.pop()
                ub -= 1
            else:
                events.append((employees[ub], 'E'))
            ub += 1

    events.sort()
    ans = 0
    last_time = 0

    for ev in events:
        if ev[1] == 'C':
            if last_time == 0 or ev[0] > last_time + d:
                ans += 1
                last_time = ev[0]

    print(ans)

if __name__ == "__main__":
    solution()
