class Solution:
    def countLargestGroup(self, n: int) -> int:

        def digit_sum(x: int) -> int:
            return sum(int(d) for d in str(x))

        groups = {}

        for i in range(1, n+1):
            digit_sum_value = digit_sum(i)
            if digit_sum_value not in groups:
                groups[digit_sum_value] = []
            groups[digit_sum_value].append(i)

        max_size = max(len(group) for group in groups.values())

        return sum(1 for group in groups.values() if len(group) == max_size)

