    def isRobotBounded(self, instructions: str) -> bool:
        x, y, direction = 0, 0, 0  # N = 0, E = 1, S = 2, W = 3 -> 1=right turn, in 2D rotation
        seen = set()

        for instruction in instructions:
            if instruction == 'G':
                if direction == 0:
                    y += 1
                elif direction == 1:
                    x += 1
                elif direction == 2:
                    y -= 1
                else:
                    x -= 1
            elif instruction == 'L':
                direction = (direction + 3 * 1) % 4  # Turning left 90 degrees
            else:  # instruction == 'R'
                direction = (direction + 1) % 4  # Turning right 90 degrees

            if (x, y, direction) in seen:
                return True
            else:
                seen.add((x, y, direction))

        return False

