class ThroneInheritance:
    def __init__(self, kingName: str):
        self.kingName = kingName
        self.inheritanceOrder = [kingName]
        self.birthRegistry = {kingName: []}
        self.dead = {}

    def _successor(self, curOrder, person):
        first = curOrder[0]
        if person not in self.birthRegistry or all(child in curOrder for child in self.birthRegistry[person]):
            if person == first:
                return None
            else:
                parentName = self.birthRegistry[person][0]
                return self._successor(curOrder + [person], parentName)
        else:
            for child in self.birthRegistry[person][::-1]:
                if child not in curOrder:
                    return child

    def _isAlive(self, person):
        return person not in self.dead

    def birth(self, parentName: str, childName: str) -> None:
        if self._isAlive(parentName):
            self.inheritanceOrder.append(childName)
            self.birthRegistry[parentName].append(childName)

    def death(self, name: str) -> None:
        self.dead[name] = True

    def getInheritanceOrder(self) -> List[str]:
        aliveOrder = self._successor([], self.kingName)
        return [person for person in self.inheritanceOrder if person in aliveOrder]

