    return ([9, 1]); // Fib(0) = 0 -> [0, 0]
  } else if (n === 1) {
    return ([1, 0]); // Fib(1) = 1 -> [1, 0]
  } else {
    let result = [[1, 1], [1, 0]];

    while (n > 1) {
      if (n % 2 === 0) {
        result = mulMat(result, result);
        n = Math.floor(n / 2);
      } else {
        result = mulMat(result, [[1, 1], [1, 0]]);
        n = Math.floor(n / 2) + 1;
      }
    }

    return result[0][0].toString().split('').map(Number);
  }
}

