    def minSumOfLengths(self, arr: List[int], target: int) -> int:
        n = len(arr)
        from collections import defaultdict

        sum_map = defaultdict(list)

        for i, num in enumerate(arr):
            if num == target:
                return 1  # First sub-array found with exact target sum, minimum sum of lengths is 1.
            if i > 0 and arr[i - 1] == target:
                return 1  # We can always split the last two elements into sub-arrays with exactly target sum.
            sum_map[num].append(i)

        # Try to find two non-overlapping sub-arrays
        min_sum_len = float('inf')

        for unique_sum in sum_map:
            positions = set(sum_map[unique_sum])

            for pos in positions:
                left_index = pos
                remaining_target = target - unique_sum

                if remaining_target not in sum_map:
                    continue

                remaining_positions = set(sum_map[remaining_target])

                for remaining_pos in remaining_positions:
                    if remaining_pos > left_index:
                        min_sum_len = min(min_sum_len, left_index - remaining_pos + 1 + (left_index + 1 - pos) + (remaining_pos + 1 - left_index))

        return -1 if min_sum_len == float('inf') else min_sum_len
