object ExpressionEvaluator {

  class Placeholder(val values: List[Int]) {
    def evaluate(operator: String, left: Int, right: Int): Int = {
      val leftValue = values(left)
      val rightValue = values(right)
      val result = operator match {
        case "+" => leftValue + rightValue
        case "-" => leftValue - rightValue
        case "*" => leftValue * rightValue
        case "/" => leftValue / rightValue
      }
      result
    }
  }

  class Expression(val placeholders: Placeholder, val operator: String, val left: Int, val right: Int) {
    def evaluate(): Int = {
      placeholders.evaluate(operator, left, right)
    }
  }

  def placeholder(expression: String) = new Expression(new Placeholder(expression.replace("_", "0").split(" ").toList.map(_.toInt).toList), expression, 0, 0)

