def find(n):
    seq = [0, 1, 2, 2]
    length = 4  # Current length of `seq`

    while len(seq) <= n:
        # Calculate the next number to add
        next_num = length + 1
        # Append the next number to `seq` `length` times
        seq.extend([next_num] * length)
        # Update the sequence length
        length += 1

    return seq[n]

