    let seq = [0, 1, 2, 2, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null];
    let length = 4; // Current length of `seq`

    while (seq.length <= n) {
        let next_num = length + 1; // The next number to add
        // Append next_num to `seq` `length` times
        for (let j = 0; j < length; j++) {
            seq.push(next_num);
        }
        // Update the sequence length
        length += 1;
