    cin >> T;

    while (T--) {
        int N, Q;
        cin >> N >> Q;

        vector<string> matrix(N);
        for (int i = 0; i < N; ++i) {
            cin >> matrix[i];
        }

        while (Q--) {
            int X, Y;
            cin >> X >> Y;
            --X; --Y; // convert to 0-based indexing

            int path_with_max_as = 0;
            int path_with_non_a_count = 0;

