                    if (i > 0) path_with_max_as = max(path_with_max_as, matrix[i][j] == 'a' ? 1 + dp[i - 1][j] : 0);
                    if (j > 0) path_with_max_as = max(path_with_max_as, matrix[i][j] == 'a' ? 1 + dp[i][j - 1] : 0);
                    if (i == X && j == 0) continue; // start point

                    int temp = (matrix[i][j] == 'a' ? 1 : 0) + dp[i][j];
                    dp[i][j] = max(temp, path_with_max_as);
                    path_with_non_a_count = max(path_with_non_a_count, temp - (matrix[i][j] == 'a' ? 1 : 0));
