from typing import List

class Solution:
    def braceExpansionII(self, expression: str) -> List[str]:
        def build(words):
            if len(words) == 1:
                return words[0]
            return [a + b for a in words[0] for b in build(words[1:])]

        def parse(exp):
            stack = []
            current = []
            for char in exp:
                if char == '{':
                    stack.append(current)
                    current = []
                elif char == '}':
                    if not stack:
                        raise ValueError("Unmatched closing brace")
                    last = stack.pop()
                    if len(last) == 1:
                        last.extend(current)
                    else:
                        last[-1].append(current)
                    current = last
                elif char == ',':
                    last_word = current
                    current = [last_word]
                else:
                    current.append(char)

            if stack:
                raise ValueError("Unmatched opening brace")

            return current

        parsed_expr = parse(expression.replace(" ", ""))

        def combine(parsed_expr):
            if isinstance(parsed_expr, list) and all(isinstance(i, list) for i in parsed_expr):
                result = []
                for left in parsed_expr:
                    for right in combine(left):
                        result.extend(combine(right))
                return result
            else:
                if isinstance(parsed_expr, str):
                    return [parsed_expr]
                if isinstance(parsed_expr, list) and all(isinstance(i, str) for i in parsed_expr):
                    result = [''.join(p) for p in permutations(parsed_expr)]
                    return result
                else:
                    return []

        from itertools import permutations
        combined_words = combine(parsed_expr)
        return sorted(set(combined_words))  # Use a set to remove duplicates and sort for the final answer.

