def sort(words):
    def merge(left, right):
        result = []
        i = j = 0
        while i < len(left) and j < len(right):
            if left[i] <= right[j]:
                result.append(left[i])
                i += 1
            else:
                result.append(right[j])
                j += 1
        result.extend(left[i:])
        result.extend(right[j:])
        return result

    def sort_recursive(words, lo, hi, alphabet):
        if hi - lo <= 1:
            return words[lo:hi]
        mid = (lo + hi) // 2
        left_sorted = sort_recursive(words, lo, mid, alphabet)
        right_sorted = sort_recursive(words, mid, hi, alphabet)
        return merge(left_sorted, right_sorted)

    def find_split(word, left, right, alphabet):
