class Solution:
    def checkPossibility(self, nums: List[int]) -> bool:
        # Iterate through the array
        for i in range(1, len(nums)):
            # Check if current element is less than the previous element
            if nums[i] < nums[i - 1]:
                # First, check if we can flip the current element to be non-decreasing
                if i == 1 or nums[i] >= nums[i - 2]:
                    nums[i] = nums[i - 1]
                else:
                    # Then check if we can flip the previous element to achieve non-decreasing array
                    nums[i - 1] = nums[i]

        return all(nums[i] >= nums[i - 1] for i in range(1, len(nums)))
