class Solution:
    def lastStoneWeightII(self, stones: List[int]) -> int:
        def lastStoneWeight(stones: List[int]) -> int:
            if not stones:
                return 0

            # Sort in descending order
            stones.sort(reverse=True)

            while len(stones) > 1:
                x = stones.pop()
                y = stones.pop()

                if x != y:
                    stones.append(y - x)
                else:
                    stones.remove(x)

            return stones[0] if stones else 0

        return lastStoneWeight(stones)

