class Solution:
    def numDupDigitsAtMostN(self, N: int) -> int:
        def findNonDup(n):
            if n < 10:
                return 0
            digits = []
            while n > 0:
                digits.append(n % 10)
                n //= 10
            for d in range(digits[-1] + 1, 10):
                found = findNonDup(int("".join(map(str, digits[:-1] + [d]))))
                if found == -1:
                    return -1
            return found

        return N - findNonDup(N) - 1 if N > 9 else N

