class RomanNumerals:

    def __init__(self):
        self.roman_map = {
            1000: 'M',
            900: 'CM',
            500: 'D',
            400: 'CD',
            100: 'C',
            90: 'XC',
            50: 'L',
            40: 'XL',
            10: 'X',
            9: 'IX',
            5: 'V',
            4: 'IV',
            1: 'I'
        }

    def is_valid_roman_numeral(self, roman):
        value = 0
        prev_value = 0
        valid_roman_numerals = ['M', 'D', 'C', 'L', 'X', 'V', 'I']
        i = 0
        while i < len(roman):
            char = roman[i]
            if char not in valid_roman_numerals:
                return False
            current_value = self.roman_map[roman[i]]
            if prev_value < current_value:
                if current_value in [1000, 900, 500, 400, 100, 90, 50, 40, 10, 9]:
                    if i + 1 < len(roman) and self.roman_map[roman[i + 1]] == str(prev_value + current_value):
                        value += current_value - prev_value
                        i += 1
                    else:
                        return False
                else:
                    value += current_value
            else:
                value += current_value
            prev_value = current_value
            i += 1
        return True

    def to_roman(self, n):
        if not isinstance(n, int) or n <= 0:
            raise ValueError("Input must be a positive integer.")

        roman_numerals = []
        for value in sorted(self.roman_map.keys(), reverse=True):
            while n >= value:
                roman_numerals.append(self.roman_map[value])
                n -= value

        return ''.join(roman_numerals) or 'nulla'

    def from_roman(self, roman):
        if not isinstance(roman, str) or not self.is_valid_roman_numeral(roman):
            raise ValueError("Invalid Roman numeral string.")

        result = 0
        prev_value = 0
        for char in reversed(roman):
            current_value = self.roman_map[char]
            if prev_value < current_value:
                result += current_value - prev_value
            else:
                result += current_value
            prev_value = current_value
        return result

