class Solution:
    def verticalTraversal(self, root: TreeNode) -> List[List[int]]:
        from collections import defaultdict

        def verticalOrder(root, x, y, m, n, res):
            if root:
                if x not in m:
                    m[x] = []
                    n[x] = float('inf')
                if n[x] > y:
                    m[x].append(root.val)
                    n[x] = y
                verticalOrder(root.left, x - 1, y - 1, m, n, res)
                verticalOrder(root.right, x + 1, y - 1, m, n, res)

        result = []
