def min_toggles_to_compress(n, m, image):
    mod = 10**9 + 7
    inf = float('inf')

    def f(k):
        if n < k or m < k:
            return inf
        l, r, b, c = 1, 0, 0, 0
        for j in range(k):
            w = 0
            for i in range(j, n, k):
                if r <= m - j:
                    w += image[i][r] - 1
                else:
                    w += 0 - 1
                r += 1
            if w > 0:
                b += 1
            w = 0
            for i in range(j, n, k):
                if l <= m - j:
                    w += image[i][l] - 1
                else:
                    w += 0 - 1
                l += 1
            if w > 0:
                c += 1
        return min(b, c)

    ans = inf
    for k in range(2, max(n, m) + 1):
        ans = min(ans, f(k))
    return ans

