            if line == '':
                index += 1
                continue
            board.append(list(map(int, line.split())))
            index += 1

        if len(board) == 3:
            initial_state = tuple(sum(board[i], []) for i in range(3))
            result = bfs(initial_state, tuple(range(1, 10)))
            results.append(str(result))

        if len(board) < 3:
            board.append([''] * 3)
            for i in range(3):
                board[i].append(0)
            initial_state = tuple(sum(board[i], []) for i in range(4))
            result = bfs(initial_state, tuple(range(1, 10)))
            results.append(str(result))

    print(" ".join(results))

