def palindrome(num):
    from typing import List, Union

    if not isinstance(num, int) or num < 0:
        return "Not valid"

    str_num = str(num)
    single_length_palindromes = set()
    double_length_palindromes = set()

    def is_valid_palindrome(s: str) -> bool:
        return s != s[::-1] and '0' not in s[0] and '0' not in s[-1] and s[0] != '0'

    for i in range(len(str_num)):
        for j in range(i + 1, len(str_num) + 1):
            pal = str_num[i:j]
            if is_valid_palindrome(pal):
                if len(pal) == 1:
                    single_length_palindromes.add(int(pal))
                elif len(pal) == 2:
                    double_length_palindromes.add(int(pal))

    if not single_length_palindromes and not double_length_palindromes:
        return "No palindromes found"

    pal_list = sorted(list(single_length_palindromes) + list(double_length_palindromes))
    return pal_list if pal_list else "No palindromes found"
