class Solution:
    def asteroidCollision(self, asteroids: List[int]) -> List[int]:
        stack = []

        for asteroid in asteroids:
            if not stack or stack[-1] >= 0 or stack[-1] < abs(asteroid):
                stack.append(asteroid)
            else:
                if abs(stack[-1]) == abs(asteroid):
                    stack.pop()
                elif stack[-1] < 0:
                    stack[-1] = -asteroid
                else:
                    continue

        return stack

